from __future__ import absolute_import

from apple_certifi import wrapt

from .core import where


# noinspection PyUnusedLocal
def wrap_where(wrapped, instance, args, kwargs):
    return where()


# Just patch the bundled certifi in pip >= 9.0.2
@wrapt.when_imported("pip._vendor.requests.certs")
def apply_patches(certs):
    wrapt.wrap_function_wrapper(certs, "where", wrap_where)
