import unittest

from interlinked.core.workflows.review_pull_requests.workflow import PRReviewer



class TestPRReviewer(unittest.TestCase):
    """
    To run: `python3 -m unittest interlinked.core.workflows.review_pull_requests.tests`
    """

    def test_comments(self):
        """
        Tests handling of added comments
        """

        # ➡️ 1. Comment
        PRReviewer.handle_github_event(event='issue_comment', data={

            'comment': {

                'body': '@ai-all, thoughts?',
                'html_url': 'https://github.pie.apple.com/interlinked/interlinked/pull/2#issuecomment-29628429',
            },
            'repository': {'html_url': 'https://github.pie.apple.com/interlinked/interlinked'},
            'issue': {'pull_request': {'html_url': 'https://github.pie.apple.com/interlinked/interlinked/pull/2'}},

        })